/**
 * FFI-compatible error type for all operations.
 *
 * This enum wraps internal errors into a format suitable for FFI,
 * with simple field types that can cross the language boundary.
 *
 * Note: We use `msg` instead of `message` to avoid conflicts with
 * Kotlin's Exception.message property in generated bindings.
 */
sealed class FfiException: kotlin.Exception() {
    
    /**
     * gRPC communication error.
     */
    class Grpc(
        
        /**
         * Error message from the gRPC layer.
         */
        val `msg`: kotlin.String, 
        
        /**
         * gRPC status code as integer.
         */
        val `code`: kotlin.Int
        ) : FfiException() {
        override val message
            get() = "msg=${ `msg` }, code=${ `code` }"
    }
    
    /**
     * Operation timed out.
     */
    class Timeout(
        
        /**
         * Description of what timed out.
         */
        val `msg`: kotlin.String
        ) : FfiException() {
        override val message
            get() = "msg=${ `msg` }"
    }
    
    /**
     * Serialization or deserialization error.
     */
    class Serialization(
        
        /**
         * Description of the serialization error.
         */
        val `msg`: kotlin.String
        ) : FfiException() {
        override val message
            get() = "msg=${ `msg` }"
    }
    
    /**
     * Determinism violation detected during replay.
     */
    class DeterminismViolation(
        
        /**
         * Description of what violated determinism.
         */
        val `msg`: kotlin.String
        ) : FfiException() {
        override val message
            get() = "msg=${ `msg` }"
    }
    
    /**
     * Invalid configuration provided.
     */
    class InvalidConfiguration(
        
        /**
         * Description of the configuration error.
         */
        val `msg`: kotlin.String
        ) : FfiException() {
        override val message
            get() = "msg=${ `msg` }"
    }
    
    /**
     * Operation was cancelled.
     */
    class Cancelled(
        ) : FfiException() {
        override val message
            get() = ""
    }
    
    /**
     * Worker is shutting down.
     */
    class ShuttingDown(
        ) : FfiException() {
        override val message
            get() = ""
    }
    
    /**
     * No work available (poll returned empty).
     */
    class NoWorkAvailable(
        ) : FfiException() {
        override val message
            get() = ""
    }
    
    /**
     * Generic error for other cases.
     */
    class Other(
        
        /**
         * Error message.
         */
        val `msg`: kotlin.String
        ) : FfiException() {
        override val message
            get() = "msg=${ `msg` }"
    }
    

    companion object ErrorHandler : UniffiRustCallStatusErrorHandler<FfiException> {
        override fun lift(error_buf: RustBuffer.ByValue): FfiException = FfiConverterTypeFfiError.lift(error_buf)
    }

    
}

